import React from 'react'

const Avatar = ({children, backgrountColor, px, py, color, borderRadius, frontSize, textAlign, cursor}) => {
  const style ={
    backgrountColor,
    padding: `${py} ${px}`,
    color: color || 'black',
    borderRadius,
    frontSize,
    textAlign: "center",
    cursor: cursor || null,
    textDecoration: "none"
     }


  return (
    <div style={style}>
      {children}
    </div>
  )
}

export default Avatar
